(5, 1, '2019-02-11 07:07:43', '2019-02-11 07:07:43', '{\"id\":5,\"form_field_id\":\"1\",\"form_fields\":{\"fullname\":{\"id\":\"fullname\",\"type\":\"text\",\"label\":\"Name\",\"meta-key\":\"name\",\"description\":\"\",\"required\":\"1\",\"placeholder\":\"\",\"css\":\"\"},\"email\":{\"id\":\"email\",\"type\":\"email\",\"label\":\"Email\",\"meta-key\":\"email\",\"description\":\"\",\"required\":\"1\",\"placeholder\":\"\",\"default_value\":\"\",\"css\":\"\"},\"subject\":{\"id\":\"subject\",\"type\":\"text\",\"label\":\"Subject\",\"meta-key\":\"subject\",\"description\":\"\",\"required\":\"1\",\"placeholder\":\"\",\"css\":\"\"},\"message\":{\"id\":\"message\",\"type\":\"textarea\",\"label\":\"Message\",\"meta-key\":\"message\",\"description\":\"\",\"placeholder\":\"\",\"css\":\"\"}},\"settings\":{\"form_title\":\"Contact Form\",\"form_desc\":\"\",\"successful_form_submission_message\":\"Thanks for contacting us! We will be in touch with you shortly.\",\"redirect_to\":\"0\",\"custom_page\":\"2\",\"external_url\":\"\",\"layout_class\":\"default\",\"form_class\":\"\",\"submit_button_text\":\"Submit\",\"email\":{\"connection_1\":{\"connection_name\":\"Admin Notification\",\"evf_to_email\":\"osmanforhad93@gmail.com\",\"evf_from_name\":\"News\",\"evf_from_email\":\"osmanforhad93@gmail.com\",\"evf_reply_to\":\"{field_id=\\\"email\\\"}\",\"evf_email_subject\":\"New Form Entry \",\"evf_email_message\":\"{all_fields}\"}}},\"structure\":{\"row_1\":{\"grid_1\":[\"fullname\",\"email\",\"subject\",\"message\"]}}}', 'Contact Form', '', 'publish', 'closed', 'closed', '', 'contact-form', '', '', '2019-02-11 07:07:43', '2019-02-11 07:07:43', '', 0, 'http://localhost/News-portal-wp/wordpress/?post_type=everest_form&#038;p=5', 0, 'everest_form', '', 0),
